entityWorkboardByTimestamp.reduce:
function (keys, values, rereduce) {
  
   var json = {
      expCnt : 0,
      trnCnt : 0,
      settlementCnt : 0,
      E_settlementCnt : 0,
      refundCnt : 0,
      disputeCnt :0,
      E_refundCnt : 0,
      E_disputeCnt :0,
	    conflict: 0,
	    lastSettlementAmount: 0
   }
  
  for (var i=0;i<values.length;i++){
      json.expCnt += values[i].expCnt;
      json.trnCnt += values[i].trnCnt;
      json.settlementCnt += values[i].settlementCnt;
      json.E_settlementCnt += values[i].E_settlementCnt;
      json.refundCnt += values[i].refundCnt;
      json.disputeCnt += values[i].disputeCnt;
      json.E_refundCnt += values[i].E_refundCnt;
      json.E_disputeCnt += values[i].E_disputeCnt;
	  json.conflict += values[i].conflict;
	  json.lastSettlementAmount += values[i].lastSettlementAmount;
  }
  
  return json; 
 }

entityWorkboardByTimestamp.map:
function (doc) {
  
   var json = {
      expCnt : 0,
      trnCnt : 0,
      settlementCnt : 0,
      E_settlementCnt : 0,
      refundCnt : 0,
      E_refundCnt : 0,
      disputeCnt :0,
      E_disputeCnt : 0,
      conflict : 0,
      lastSettlementAmount: 0
   }
   var doEmit = false;
   var d = "";
   var docTimeStamp = 0;
   if (doc.data.DocumentName == "Settlement"){
      docTimeStamp = doc.data.StartDate
   }
   else {
     docTimeStamp = doc.data.TimeStamp;
   }
   
   if (docTimeStamp) {
       var t = new Date(docTimeStamp * 1000)
       var year = t.getFullYear();
       var mm = t.getMonth() + 1
       mm = mm.toString() ;
       if (mm.length==1){
           mm = "0"+mm;
       }
       var dd = t.getDate() 
       dd = dd.toString();
       if (dd.length==1){
          dd = "0"+dd;
       }
       d = year + mm + dd;
       d=parseInt(d)
   }
   if (doc.data.DocumentName == "Settlement") {
		if(doc.data.Status  == "Approved" || doc.data.Status  == "Processed")
		{
			lastSettlementAmount = doc.data.ReconciledAmount - doc.data.ExceptionAmount - doc.data.RefundAmount;
		}
	
	}
   
   if(doc.data.DocumentName =="ConsolidatedView"){
       
	   if(doc.data.ConflictCode.IsAcquirerConflictAmount || doc.data.ConflictCode.IsEntityConflictAmount
	   || doc.data.ConflictCode.IsStatusConflict)
	   {
			json.conflict = 1;
	   
	   }
	   else
	   {
		var exp = (doc.data.ISException) ? 1 : 0
		var trn = (doc.data.ISException) ? 0 : 1
		json.expCnt = exp;
		json.trnCnt = trn;
	   }
       if (doc.data.TimeStamp && doc.data.SPCode){
          doEmit = true;
       }
   }
   if(doc.data.DocumentName == "RefundView"){
      if(doc.data.Status =="Initiated" ){
        if (doc.data.TimeStamp && doc.data.SPCode){
           json.refundCnt = 1
           doEmit = true
         }
      }
      if(doc.data.Status =="Processed" ||  doc.data.Status =="Rejected"){
        if (doc.data.TimeStamp && doc.data.SPCode){
           json.E_refundCnt = 1
           doEmit = true
         }
      }
   }
   
   if(doc.data.DocumentName == "Settlement"){
        if (doc.data.Status == "Initiated"){
            if (doc.data.StartDate && doc.data.SPCode){
                 json.settlementCnt = 1
                 doEmit = true
            }  
        }
        if (doc.data.Status == "Approved"){
            if (doc.data.TimeStamp && doc.data.SPCode){
                 json.E_settlementCnt = 1
                 doEmit = true
            }  
        }
      
   }   
   
   if(doc.data.DocumentName == "DisputeView"){
      if(doc.data.Status =="Initiated" ){
        if (doc.data.TimeStamp && doc.data.SPCode){
           json.disputeCnt = 1
           doEmit = true
         }
      }
      if(doc.data.Status =="Forwarded" ){
        if (doc.data.TimeStamp && doc.data.SPCode){
           json.E_disputeCnt = 1
           doEmit = true
         }
      }
   }
   
   if (doEmit){
      emit([d,doc.data.SPCode], json)
   }
}
