getTransByMonth.reduce:
function (keys, values, rereduce) {
  var reduceObj = {count :0,amount :0.00}
  for(var j=0;j<values.length;j++){
    reduceObj.count += rereduce ? values[j].count : 1;
    reduceObj.amount +=values[j].amount
  }
   return reduceObj;
}

getTransByMonth.map:
function (doc) {

  if (doc.data.DocumentName == "ConsolidatedView" && doc.data.TimeStamp >0 && doc.data.SPCode != "") {
       var date = new Date(doc.data.TimeStamp * 1000);
       var startOfMonth = new Date(date.getFullYear(), date.getMonth());
       var timeStamp = startOfMonth / 1000;
       emit([timeStamp,doc.data.SPCode], {count :1, amount : doc.data.TotalBillAmount});
       //emit ([doc.data.SPCode],1)
  }
}
