function (doc) {
  if(doc.data.DocumentName === "ConsolidatedView"){
    var now = new Date(doc.data.TimeStamp * 1000);
    var startOfDay = new Date(now.getFullYear(), now.getMonth(), now.getDate());
    var timestamp = startOfDay / 1000;
    var status = "SUCCESS";
    if(doc.data.ISRFND){status = "REFUNDED";}
    if(doc.data.ISFAIL){status = "FAILED";}
    var value =  {amount: doc.data.TotalBillAmount,count : 1};
    var keys = [timestamp,doc.data.SPCode,doc.data.ServiceCode,status,doc.data.CardType,doc.data.PaymentMethod,doc.data.PaymentGateway,doc.data.AcquirerId];
    emit(keys,value);
  }
}
