transactionSummary.reduce:
function (keys, values, rereduce) {
  var reduceObj = {count :0,amount :0.00}
  for(var j=0;j<values.length;j++){
    reduceObj.count += rereduce ? values[j].count : 1;
    reduceObj.amount +=values[j].amount
  }
   return reduceObj;
}

transactionSummary.map:
function (doc) {
    if(doc.DocumentName === "ConsolidatedView"){
        var now = new Date(doc.TimeStamp * 1000);
        var startOfDay = new Date(now.getFullYear(), now.getMonth(), now.getDate());
        var timestamp = startOfDay / 1000;
        var status = "SUCCESS";
        if(doc.ISRFND){status = "REFUNDED";}
        if(doc.ISFAIL){status = "FAILED";}
        var value =  {amount: doc.TotalBillAmount,count : 1};
        // please don't change the keys indexes
        var keys = [
            timestamp,
            doc.SPCode,
            doc.ServiceCode,
            status,
            doc.AcquirerId,
            doc.PaymentGateway,
            doc.PaymentMethod,
            doc.CardType,
            doc.EPayStatusAuthRecv
        ];
        emit(keys,value);
    }
}
