transactionSummary.reduce:
function (keys, values, rereduce) {
  var reduceObj = {count :0,amount :0.00}
  for(var j=0;j<values.length;j++){
    reduceObj.count += rereduce ? values[j].count : 1;
    reduceObj.amount +=values[j].amount
  }
   return reduceObj;
}

transactionSummary.map:
function (doc) {
  if(doc.data.DocumentName === "ConsolidatedView"){
    var now = new Date(doc.data.TimeStamp * 1000);
    var startOfDay = new Date(now.getFullYear(), now.getMonth(), now.getDate());
    var timestamp = startOfDay / 1000;
    var status = "SUCCESS";
    if(doc.data.ISRFND){status = "REFUNDED";}
    if(doc.data.ISFAIL){status = "FAILED";}
    var value =  {amount: doc.data.TotalBillAmount,count : 1};
    // please don't change the keys indexes
    var keys = [
      timestamp, 
      doc.data.SPCode, 
      doc.data.ServiceCode, 
      status, 
      doc.data.AcquirerId, 
      doc.data.PaymentGateway, 
      doc.data.PaymentMethod, 
      doc.data.CardType,
      doc.data.EPayStatusAuthRecv
      ];
    emit(keys,value);
  }
}
